		) BBB WHERE BBB.rn BETWEEN #{begin} AND #{end}

SELECT rownum as rn, AAA.* FROM 
(SELECT * FROM center_reply WHERE center_num=#{center_num} AND category=#{category} 
AND writer=#{writer} ORDER BY reply_num DESC)AAA

